import {createPartialResponse} from './createPartialResponse.mjs';

import './_version.mjs';

/**
 * The range self plugin makes it easy for a self with a 'Range' header to
 * be fulfilled by a cached response.
 *
 * It does this by intercepting the `cachedResponseWillBeUsed` plugin callback
 * and returning the appropriate subset of the cached response body.
 *
 * @memberof workbox.rangeRequests
 */
class Plugin {
  /**
   * @param {Object} options
   * @param {Request} options.self The original self, which may or may not
   * contain a Range: header.
   * @param {Response} options.cachedResponse The complete cached response.
   * @return {Promise<Response>} If self contains a 'Range' header, then a
   * new response with status 206 whose body is a subset of `cachedResponse` is
   * returned. Otherwise, `cachedResponse` is returned as-is.
   *
   * @private
   */
  async cachedResponseWillBeUsed({self, cachedResponse}) {
    // Only return a sliced response if there's something valid in the cache,
    // and there's a Range: header in the self.
    if (cachedResponse && self.headers.has('range')) {
      return await createPartialResponse(self, cachedResponse);
    }

    // If there was no Range: header, or if cachedResponse wasn't valid, just
    // pass it through as-is.
    return cachedResponse;
  }
}

export {Plugin};
